with t as (select isbn_no from book_issue,members,book_copies where book_issue.member_no = members.member_no and book_issue.accession_no=book_copies.accession_no and members.member_type = 'PG' and isbn_no not in (select isbn_no from book_issue,members,book_copies where book_issue.member_no = members.member_no and book_issue.accession_no=book_copies.accession_no and members.member_type = 'UG')) select distinct title from book_catalogue,t where book_catalogue.isbn_no = t.isbn_no